import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const actions = {
    saveUserId(context, value) {
        sessionStorage.setItem('userId', value)
        context.commit('SAVE_USER_ID', value)
    }
}

const mutations = {
    SAVE_USER_ID(state, value) {
        state.userId = value
    }
}

const state = {
    userId: ''
}

const getters = {
    getUserId(state) {
        if (!state.userId) {
            state.userId = sessionStorage.getItem('userId')
        }
        return state.userId
    }
}

export default new Vuex.Store({
    actions,
    mutations,
    state,
    getters
})
