export default {
  name: 'MarkdownView',
  data() {
    return {
      text: ''
    };
  },
  methods: {
    getNoteInfo() {
      console.log(this.$route.params.location);
      this.$axios.get('/Note/GetNoteInfo?fileLocation=' + this.$route.params.location).then(response => {
        console.log(response.data);
        if (response.data.flag) {
          this.text = response.data.data;
        } else {
          this.$message.error(response.data.msg);
        }
      });
    }
  },
  mounted() {
    this.getNoteInfo();
  }
};
