import VueRouter from 'vue-router'

import ContainerPage from '@/pages/ContainerPage'
import LoginPage from '@/pages/LoginPage'
import RegisterPage from '@/pages/RegisterPage'
import IndexPage from '@/pages/IndexPage'
import NoteManage from '@/pages/NoteManage'
import PlanManage from '@/pages/PlanManage'
import StudyProgress from '@/pages/StudyProgress'
import ManagePage from '@/pages/ManagePage'
import UserPage from '@/pages/UserPage'
import UserInfo from '@/pages/UserInfo'
import NoteTypes from '@/pages/NoteTypes'
import page404 from '@/pages/page404'

const router =  new VueRouter({
    routes: [
        {
            name: '',
            path: '/',
            component: ContainerPage,
            children: [
                {
                    path: 'manage',
                    component: ManagePage,
                    children: [
                        {
                            path: 'index',
                            component: IndexPage
                        },
                        {
                            path: 'note',
                            component: NoteManage
                        },
                        {
                            path: 'plan',
                            component: PlanManage
                        },
                        {
                            path: 'study',
                            component: StudyProgress
                        }
                    ]
                },
                {
                    path: 'account',
                    component: UserPage,
                    children: [
                        {
                            path: 'info',
                            component: UserInfo
                        },
                        {
                            path: 'types',
                            component: NoteTypes
                        }
                    ]
                }
            ]
        },
        {
            path: '/login',
            component: LoginPage
        },
        {
            path: '/register',
            component: RegisterPage
        },
        {
            path: '*',
            component: page404
        }
    ]
})

router.beforeEach((to, from, next) => {
    console.log(to);
    if (to.path.startsWith('/manage') && to.path.startsWith('/accout')) {
        if (this.$route.getters['getUserId']()) {
            console.log(123);
        }
        console.log(this.$route.getters['getUserId']);
    }
    next()
})

export default router
